syntax_editor_macro.text:
	let durationInSeconds = current.stop_date_time - current.start_date_time;
	let durationHours = Math.floor(durationInSeconds / 3600); //truncated number of hours
	let durationMinutes = Math.floor((durationInSeconds % 3600) / 60); 
	//modulo operator finds the remaining second after calculating hours and divide by 60 top get minutes

	return '${durationHours}:${durationMinutes}';  // return the calculated value
